#!/bin/bash

# AI 有声小说系统部署脚本
# 使用方法: ./deploy.sh [start|stop|restart|logs|status|backup]

set -e

# 颜色定义
RED='\033[0;31m'
GREEN='\033[0;32m'
YELLOW='\033[1;33m'
BLUE='\033[0;34m'
NC='\033[0m' # No Color

# 项目信息
PROJECT_NAME="ai-novel"
COMPOSE_FILE="docker-compose.yml"
BACKUP_DIR="./backups"

# 日志函数
log_info() {
    echo -e "${BLUE}[INFO]${NC} $1"
}

log_success() {
    echo -e "${GREEN}[SUCCESS]${NC} $1"
}

log_warning() {
    echo -e "${YELLOW}[WARNING]${NC} $1"
}

log_error() {
    echo -e "${RED}[ERROR]${NC} $1"
}

# 检查依赖
check_dependencies() {
    log_info "检查系统依赖..."

    # 检查 Docker
    if ! command -v docker &> /dev/null; then
        log_error "Docker 未安装，请先安装 Docker"
        exit 1
    fi

    # 检查 Docker Compose
    if ! command -v docker-compose &> /dev/null; then
        log_error "Docker Compose 未安装，请先安装 Docker Compose"
        exit 1
    fi

    log_success "依赖检查完成"
}

# 检查环境配置
check_environment() {
    log_info "检查环境配置..."

    if [ ! -f ".env" ]; then
        log_warning ".env 文件不存在，从模板创建..."
        cp backend/.env.example .env
        log_warning "请编辑 .env 文件配置必要的环境变量"
        return 1
    fi

    # 检查必要的环境变量
    if ! grep -q "ENCRYPTION_KEY=" .env; then
        log_warning "未设置 ENCRYPTION_KEY，请手动配置"
    fi

    log_success "环境配置检查完成"
}

# 创建备份目录
create_backup_dir() {
    if [ ! -d "$BACKUP_DIR" ]; then
        mkdir -p "$BACKUP_DIR"
        log_info "创建备份目录: $BACKUP_DIR"
    fi
}

# 启动服务
start_services() {
    log_info "启动 $PROJECT_NAME 服务..."

    check_dependencies
    check_environment
    create_backup_dir

    # 拉取最新代码（如果是 Git 仓库）
    if [ -d ".git" ]; then
        log_info "拉取最新代码..."
        git pull origin main || log_warning "无法拉取代码，继续使用本地版本"
    fi

    # 构建并启动服务
    docker-compose -f $COMPOSE_FILE up -d --build

    # 等待服务启动
    log_info "等待服务启动..."
    sleep 10

    # 检查服务状态
    if docker-compose -f $COMPOSE_FILE ps | grep -q "Up"; then
        log_success "服务启动成功！"
        show_service_info
    else
        log_error "服务启动失败，请检查日志"
        docker-compose -f $COMPOSE_FILE logs
        exit 1
    fi
}

# 停止服务
stop_services() {
    log_info "停止 $PROJECT_NAME 服务..."

    docker-compose -f $COMPOSE_FILE down

    log_success "服务已停止"
}

# 重启服务
restart_services() {
    log_info "重启 $PROJECT_NAME 服务..."

    stop_services
    sleep 5
    start_services
}

# 查看日志
show_logs() {
    local service=$1
    local follow=$2

    if [ -n "$service" ]; then
        if [ "$follow" = "-f" ]; then
            docker-compose -f $COMPOSE_FILE logs -f $service
        else
            docker-compose -f $COMPOSE_FILE logs $service
        fi
    else
        docker-compose -f $COMPOSE_FILE logs
    fi
}

# 查看服务状态
show_status() {
    log_info "$PROJECT_NAME 服务状态:"
    echo
    docker-compose -f $COMPOSE_FILE ps
    echo

    # 显示健康检查状态
    log_info "健康检查状态:"
    echo
    for container in $(docker-compose -f $COMPOSE_FILE ps -q); do
        name=$(docker inspect --format='{{.Name}}' $container | sed 's/\///g')
        health=$(docker inspect --format='{{.State.Health.Status}}' $container 2>/dev/null || "unknown")
        status=$(docker inspect --format='{{.State.Status}}' $container)

        if [ "$health" = "unknown" ]; then
            echo -e "$name: ${YELLOW}$status${NC} (无健康检查)"
        elif [ "$health" = "healthy" ]; then
            echo -e "$name: ${GREEN}healthy${NC}"
        else
            echo -e "$name: ${RED}$health${NC}"
        fi
    done
}

# 显示服务信息
show_service_info() {
