echo "前端应用: http://localhost"
echo "后端API: http://localhost/api"
}

# 备份数据
backup_data() {
    log_info "备份项目数据..."

    DATE=$(date +%Y%m%d_%H%M%S)
    BACKUP_FILE="${BACKUP_DIR}/ai-novel-backup_${DATE}.tar.gz"

    docker-compose exec backend tar czf /backup/${BACKUP_FILE} /app/data

    log_success "数据已备份到: $BACKUP_FILE"
}

# 清理资源
cleanup() {
    log_info "清理 Docker 资源..."

    # 清理未使用的镜像
    docker image prune -f

    # 清理未使用的卷
    docker volume prune -f

    # 清理构建缓存
    docker builder prune -f

    log_success "清理完成"
}

# 主函数
main() {
    local command=$1

    case $command in
        start)
            start_services
            ;;
        stop)
            stop_services
            ;;
        restart)
            restart_services
            ;;
        logs)
            show_logs $2 $3
            ;;
        status)
            show_status
            ;;
        backup)
            backup_data
            ;;
        cleanup)
            cleanup
            ;;
        *)
            echo "使用方法: $0 [start|stop|restart|logs|status|backup|cleanup]"
            echo
            echo "命令说明:"
            echo "  start    - 启动所有服务"
            echo "  stop     - 停止所有服务"
            echo "  restart  - 重启所有服务"
            echo "  logs     - 查看日志 (可指定服务名)"
            echo "  status   - 查看服务状态"
            echo "  backup   - 备份数据"
            echo "  cleanup  - 清理资源"
            exit 1
            ;;
    esac
}

# 脚本入口
main "$@"